import React from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { Card, Spinner, Container, Row, Col } from 'react-bootstrap';

//Static Routes
import Login from './Static/Login';
import Home from './Static/Home';
import About from './Static/About';
import Contact from './Static/Contact';

import PrivateRoute from './Layout/PrivateRoute';
import Layout from './Layout/Layout';

import Dashboard from './User/Dashboard';

import PodsList from './Pods/PodsList';
import OffersList from './Offers/OffersList';

function App(props) {
  const auth = props.auth;
  const notSignedIt = auth.isEmpty;

  const loadingBoxStyle = {
    display: 'flex',
    height: '150px',
    width: '100%',
    background: 'linear-gradient(180deg, rgba(0,0,0,0.05) 0%, rgba(255,255,255,1) 78%)',
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: '4rem'
  };

  if (auth.isLoaded) {
    return (
      <Router>
        <Layout login={notSignedIt}>
          <Redirect from={'/'} to={'/home'} />
          <Route path="/home" component={Home} />
          <Route path="/contact" component={Contact} />
          <Route path="/about" component={About} />
          <Route path="/login" component={Login} />

          {/* PRIVATE ROUTE  */}
          <PrivateRoute path="/dashboard" authenticated={notSignedIt} component={Dashboard} />
          <PrivateRoute path="/podslist" authenticated={notSignedIt} component={PodsList} />
          <PrivateRoute path="/postslist" authenticated={notSignedIt} component={OffersList} />
        </Layout>
      </Router>
    );
  } else {
    return (
      /////////////  LOADING SPINNER WAITING ON FIREBASE AUTH ///////////////
      <Container>
        <Row>
          <Col md={{ span: 6, offset: 3 }}>
            <Card>
              <div style={loadingBoxStyle} className="text-center">
                <Spinner animation="border" role="status" size="lg" className="text-center" size="lg" variant="primary">
                  <span className="sr-only">Loading...</span>
                </Spinner>
              </div>
            </Card>
          </Col>
        </Row>
      </Container>
    );
  }
}

// eslint-disable-next-line
App = connect(
  // Map redux state to component props
  ({ firebase: { auth, profile } }) => ({
    auth,
    profile
  })
)(App);
export default App;
